INSERT INTO `paas_file` VALUES (24, 2, '_code', '', '/_code/java_model_ibinder', -1, 1, 1, 0, 0, NULL, 'freemarker', '', 'import lombok.Getter;\nimport lombok.Setter;\nimport org.noear.weed.GetHandlerEx;\nimport org.noear.weed.IBinder;\nimport java.math.BigDecimal;\nimport java.util.*;\n\n@Getter\n@Setter\npublic class ${table_camel!}Model implements IBinder {\n    \n    <#list fields!! as f>\n /** ${(f.comment)!} */\n    public ${(f.type)!} ${(f.field)!};\n    </#list>\n\n    @Override\n public void bind(GetHandlerEx s) {\n        <#list fields as f>\n        ${(f.field)!} = s.get(\"${(f.field)!}\").value(${(f.def)!});\n        </#list>\n }\n \n  @Override\n public IBinder clone() {\n    return new ${table_camel!}Model();\n  }\n\n}', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:45:58', '2020-08-19 16:15:06', NULL);
INSERT INTO `paas_file` VALUES (25, 2, '_code', '', '/_code/java_edit', 0, 1, 1, 0, 0, NULL, 'freemarker', '', 'public static void edit${table_camel!} (<#list fields!! as f>${(f.type)!} ${(f.field)!}<#sep>,\n                             </#sep></#list>) throws SQLException {\n                                 \n    DbTableQuery query = db().table(\"${tb!}\")<#list fields!! as f><#if (f.field)! != pri_key!>.set(\"${(f.field)!}\", ${(f.field)!})</#if><#sep>\n                             </#sep></#list>;\n\n    if (${pri_key!} > 0) {\n        query.where(\"${pri_key!} = ?\", ${pri_key!})\n             .update();\n    } else {\n        query.insert();\n    }\n\n}\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:46:29', '2020-01-17 14:46:37', NULL);
INSERT INTO `paas_file` VALUES (26, 2, '_code', '', '/_code/java_edit2', 0, 1, 1, 0, 0, NULL, 'freemarker', '', 'public static void edit${table_camel!} (${table_camel!}Model m) throws SQLException {\n                                 \n    DbTableQuery qr = db().table(\"${tb!}\")\n                          .setEntity(m);\n\n    if (${pri_key!} > 0) {\n        qr.where(\"${pri_key!} = ?\", ${pri_key!})\n          .update();\n    } else {\n        qr.insert();\n    }\n\n}\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:46:51', '2020-01-17 14:47:02', NULL);
INSERT INTO `paas_file` VALUES (27, 2, '_code', '', '/_code/java_crud', 0, 1, 1, 0, 0, NULL, 'freemarker', '', 'public static void save${table_camel!} (${table_camel!}Model m) throws Exception {\n                                 \n    DbTableQuery qr = db().table(\"${tb!}\")\n                          .setEntity(m);\n\n    if (m.${pri_key!} > 0) {\n        qr.where(\"${pri_key!} = ?\", m.${pri_key!})\n          .update();\n    } else {\n        qr.set(\"operator\",Session.current().getUserName())\n          .set(\"log_date\",Datetime.Now().getDate())\n          .set(\"log_fulltime\",Datetime.Now().getFullTime())\n          .insert();\n    }\n}\n\npublic static void del${table_camel!} (int ${pri_key!}) throws SQLException {\n                                 \n    db().table(\"${tb!}\")\n        .where(\"${pri_key!} = ?\", ${pri_key!})\n        .delete();\n}\n\npublic static ${table_camel!}Model get${table_camel!} (int ${pri_key!}) throws SQLException {\n                                 \n    return db().table(\"${tb!}\")\n              .where(\"${pri_key!} = ?\", ${pri_key!})\n              .orderBy(\"log_fulltime desc\")\n              .limit(1)\n              .select(\"*\")\n              .getItem(new ${table_camel!}Model());\n}\n\n\npublic static List<${table_camel!}Model> get${table_camel!}List (${table_camel!}Model m) throws SQLException {\n                                 \n    return db().table(\"${tb!}\")\n              .where(\"1=1\")\n              .orderBy(\"log_fulltime desc\")\n              .select(\"*\")\n              .getList(new ${table_camel!}Model());\n}\n\n\npublic static List<${table_camel!}Model> get${table_camel!}ListByPage (${table_camel!}Model m, PageModel pageModel) throws SQLException {\n                                 \n    DbTableQuery query = db().table(\"${tb!}\")\n                        .where(\"1=1\");\n    if (pageModel != null) {\n      int start = (pageModel.page - 1) * pageModel.pageSize;\n      DbTableQuery queryCount = query;\n      pageModel.setRowCount(queryCount.select(\"count(*)\").getCount());\n      return query\n          .orderBy(\"log_fulltime desc\")\n          .limit(start, pageModel.pageSize)\n          .select(\"*\")\n          .getList(new ${table_camel!}Model());\n    } else {\n      return query.orderBy(\"log_fulltime desc\").select(\"*\").getList(new ${table_camel!}Model());\n    }          \n}\n\n\n\nimport lombok.Getter;\nimport lombok.Setter;\n\n@Getter\n@Setter\npublic class PageModel {\n\n    public Integer page;\n    public Integer pageSize;\n    public long    rowCount;\n\n  public PageModel() { }\n\n  public PageModel(Integer page, Integer pageSize) {\n    this.page = (page == null || page == 0)?1:page;\n    this.pageSize = (pageSize == null || pageSize == 0)?10:pageSize;\n  }\n}\n\n\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:47:15', '2020-01-17 14:47:27', NULL);
INSERT INTO `paas_file` VALUES (28, 2, '_code', '', '/_code/java_insert', 0, 1, 1, 0, 0, NULL, 'freemarker', '', 'import noear.weed.DbQueryProcedure;\nimport webapp.Config;\n\npublic class ${tb!}_add extends DbQueryProcedure {\n\n    <#list fields!! as f>\n    public ${(f.type)!} ${(f.field)!};\n    </#list>\n\n    public ${tb!}_add() {\n\n        super(Config.${key!});\n\n        sql(\"INSERT INTO `${tb!}` (<#list fields!! as f>`${(f.field)!}`<#sep>, </#sep></#list>)\" +\n            \" VALUES (<#list fields!! as f>@${(f.field)!}<#sep>,  </#sep></#list>);\");\n\n        <#list fields!! as f>\n        set(\"@${(f.field)!}\", () -> ${(f.field)!});\n        </#list>\n\n    }\n\n}\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:47:48', '2020-01-17 14:47:54', NULL);
INSERT INTO `paas_file` VALUES (29, 2, '_code', '', '/_code/paas_stats_save', 0, 1, 1, 0, 0, NULL, 'freemarker', '', 'function insertOrUpdateResult(target_date, <#list fields!! as f><#if f.field != pri_key>${(f.field)!}<#sep>, </#sep></#if></#list>) {\n    <#list fields!! as f>\n    <#if f.field != pri_key>\n    if (!${f.field}) {\n    ${f.field} = 0;\n    }\n    </#if>\n    </#list>\n\n  var is_exist = water\n    .db(\"${tag}.${key}\")\n    .table(\"${tb}\")\n   .where(\"target_date = ?\", target_date)\n    .selectCount();\n \n  if (is_exist && 0 < is_exist) {\n   water\n            .db(\"${tag}.${key}\")\n            .table(\"${tb}\")\n            <#list fields!! as f>\n            <#if f.field != pri_key>\n            .set(\"${f.field}\", ${f.field})\n            </#if>\n            </#list>\n     .set(\"update_date\", now.getDate())\n      .set(\"update_fulltime\", now.getFulltime())\n      .where(\"target_date = ?\", target_date)\n      .update();\n  } else {\n    water\n            .db(\"${tag}.${key}\")\n            .table(\"${tb}\")\n            .set(\"${pri_key}\", rock.client.newID(\"${tag?upper_case}\", \"${tb}_id\", 315360000))\n            <#list fields!! as f>\n            <#if f.field != pri_key>\n            .set(\"${f.field}\", ${f.field})\n            </#if>\n            </#list>\n      .set(\"target_date\", target_date)\n            .set(\"create_date\", now.getDate())\n            .set(\"create_fulltime\", now.getFulltime())\n      .set(\"update_date\", now.getDate())\n      .set(\"update_fulltime\", now.getFulltime())\n      .insert();\n  }\n}', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:48:07', '2021-04-05 16:14:28', NULL);
INSERT INTO `paas_file` VALUES (30, 2, '_code', '', '/_code/weed_xml_insert', 0, 1, 1, 0, 0, NULL, 'freemarker', '', '<sql id=\"add_${tb!}\"\n     :return=\"long\"\n     :caching=\"cache_${key!}\"\n     :cacheClear=\"${tb!}_<#noparse>${</#noparse>${pri_key!}<#noparse>}</#noparse>\"\n     :remarks=\"新增${tb!}\">\n    INSERT INTO `${tb!}` (<#list fields!! as f>`${(f.field)!}`<#sep>, </#sep></#list>)\n                  VALUES (<#list fields!! as f>@{${(f.field)!}:${(f.type)!}}<#sep>, </#sep></#list>)\n</sql>', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-01-17 14:48:34', '2020-08-07 14:18:35', NULL);
