INSERT INTO `paas_file` VALUES (69, 0, '_funs', '', '/_funs/_api.clz', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'this.checkParamsIsNot0 = function(c,keys){\n    for(var i in keys){\n      if(c.paramAsInt(keys[i]) == 0){\n       c.attrMap().code = 19;\n        c.attrMap().msg = \'参数不能为0(\'+keys[i]+\')\';\n          return false;\n      }\n    }\n    \n    return true;    \n}\n\nthis.checkParamsIsOk = function(c,keys){\n    for(var i in keys){\n      if(c.param(keys[i]) == null){\n      c.attrMap().code = 19;\n      c.attrMap().msg = \'参数缺少或有错误(\'+keys[i]+\')\';\n        return false;\n      }\n    }\n    \n    return true;    \n}\n\nthis.host_isWhitelist = function(c,g){\n    var rUri = c.header(\'Referer\');\n    \n    if(!rUri){\n      c.attrMap().code = 0;\n      c.attrMap().msg  = \'ip is not web\';\n      return false;\n    }\n    \n    if(!g){\n      g = \'server\';\n    }\n    \n    var rUri2 = java.net.URI.create(rUri);\n    var host2 = rUri2.getHost();\n    \n    if(WaterClient.Whitelist.existsOfDomain(g, host2)){\n       return true;\n    }else{\n       c.attrMap().code = 0;\n       c.attrMap().msg  = \'ip is not whitelist\';\n       return false;\n    }\n}\n\nthis.ip_isWhitelist = function(c,g){\n    if(!g){\n      g = \'server\';\n    }\n    \n    var ip = XUtil.ip();\n    \n    if(WaterClient.Whitelist.existsOfIp(g,ip)){\n       return true;\n    }else{\n       c.attrMap().code = 0;\n       c.attrMap().msg  = \'ip is not whitelist\';\n       return false;\n    }    \n}\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-05-06 11:30:08', '2020-06-19 15:27:30', NULL);
