INSERT INTO `paas_file` VALUES (119, 0, 'sponge_track', '', '/sponge_track/get.h5.card.uv', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', 'var row_id= ctx.paramAsInt(\"row_id\",0);\n\n/**\n1004412：h5首页卡片\n1004382：原生首页卡片\n*/\n\nvar value =water.db(\"sponge/sponge_track\")\n                .table(\"short_url_ex_track_stat\")\n         .where(\" vi= 3\")\n                .and(\"url_id = ?\", 1004412)\n                .and(\"row_id >=?\",row_id)\n                .limit(100)\n                .select(\"row_id,uv_today,vd,vi\")\n                .getMapList();\nreturn value;', '获取h5首页卡片uv', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 12:58:57', '2020-06-15 13:00:08', NULL);
INSERT INTO `paas_file` VALUES (120, 0, 'sponge_track', '', '/sponge_track/log.day', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,date\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\",\"date\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar tag_id = ctx.paramAsInt(\"tag_id\");\nvar date   = ctx.paramAsInt(\"date\");\nvar page   = ctx.paramAsInt(\"page\");\nvar size   = ctx.paramAsInt(\"size\");\n\nif(!size){\n  size=50;\n}\n\nvar list = water.db(\"sponge/sponge_track\")\n                .table(\"short_redirect_log_30d\")\n         .where(\"log_date = ?\",date)\n                         .and(\"tag_id = ?\", tag_id)\n                         .groupBy(\"user_key\")\n                         .limit(page*size, size)\n                  .select(\"user_key AS ukey, log_date\")\n                         .getMapList();\n\nif (list && list.length > 0) {\n  return {code:1,msg:\'success\',list:list};\n} else {\n  return {code:2,msg:\'没有相关数据\',list:[]};\n}', '参数：tag_id,date', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:00:56', '2021-10-09 14:42:42', NULL);
INSERT INTO `paas_file` VALUES (121, 0, 'sponge_track', '', '/sponge_track/log.day.no.duplicate', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：log_date,tag_id\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\nif (!_api.checkParamsIsOk(ctx,[\"log_date\", \"tag_id\"])) {\n    return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"size\"])){\n    return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n    return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar page     = ctx.paramAsInt(\"page\");\nvar size     = ctx.paramAsInt(\"size\");\nvar tag_id   = ctx.paramAsInt(\'tag_id\');\nvar log_date = ctx.paramAsInt(\'log_date\');\n\nvar click_list = water.db(\"sponge/sponge_track\")\n                      .table(\"short_redirect_log_30d s\")\n                      .innerJoin(\"short_url s1\").on(\"s.url_id = s1.url_id\")\n                      .where(\"s.tag_id = ?\", tag_id).and(\"s.log_date = ?\", log_date)\n                      .groupBy(\"s.user_key,s1.url_partner_key\")\n                      .limit(page*size, size)\n                      .select(\"s.url_id,s.tag_id,s.user_id,s.user_key,s1.url_partner_key,s1.url_name,s.v5 udid\")\n                      .getMapList();\n\nif (click_list && click_list.length > 0) {\n    return {code:1, msg:\'success\', list:click_list};\n} else {\n    return {code:2,msg:\'没有相关数据\',list:[]};\n}\n', '参数：log_date,tag_id', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:06:16', '2020-10-14 16:32:56', NULL);
INSERT INTO `paas_file` VALUES (122, 0, 'sponge_track', '', '/sponge_track/log.ukey.time', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,page,size,log_date\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"size\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\n\nvar tag_id   = ctx.paramAsInt(\"tag_id\");\nvar page     = ctx.paramAsInt(\"page\");\nvar size     = ctx.paramAsInt(\"size\");\nvar log_date = ctx.paramAsInt(\"log_date\");\n\n\nvar list = water.db(\"sponge/sponge_track\")\n                .table(\"short_redirect_log_30d\")\n          .where(\"log_date = ?\",log_date).and(\"tag_id = ?\", tag_id)\n                .groupBy(\"user_key\")\n                .orderBy(\"log_id\")\n                .limit(page*size, size)\n              .select(\"user_key AS ukey, MIN(log_date) AS first_date\")\n                .getMapList();\n\nif (list && list.length > 0) {\n  return {code:1,msg:\'success\',list:list};\n} else {\n  return {code:2,msg:\'没有相关数据\',list:[]};\n}\n', '参数：tag_id,page,size,log_date', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:08:30', '2021-10-09 14:42:50', NULL);
INSERT INTO `paas_file` VALUES (123, 0, 'sponge_track', '', '/sponge_track/log.ukey.time.new', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,size,page,log_date\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\"])){\n    return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"size\"])){\n    return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n    return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\n\nvar tag_id   = ctx.paramAsInt(\"tag_id\");\nvar page     = ctx.paramAsInt(\"page\");\nvar size     = ctx.paramAsInt(\"size\");\nvar log_date = ctx.paramAsInt(\"log_date\");\n\n\nvar list = water.db(\"sponge/sponge_track\")\n                .table(\"short_redirect_log_30d\")\n                .where(\"log_date = ?\",log_date).and(\"tag_id = ?\", tag_id)\n                .groupBy(\"user_key\")\n                .orderBy(\"log_id\")\n                .limit(page*size, size)\n                .select(\"user_key AS ukey, MIN(log_date) AS first_loan_date, \" +\n                    \"MIN(log_fulltime) AS first_loan_time, MAX(log_date) AS last_loan_date, \" +\n                    \"MAX(log_fulltime) AS last_loan_time\")\n                .getMapList();\n\nif (list && list.length > 0) {\n    return {code:1,msg:\'success\',list:list};\n} else {\n    return {code:2,msg:\'没有相关数据\',list:[]};\n}', '用户首次与最近登录贷超', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:11:49', '2021-10-09 14:43:00', NULL);
INSERT INTO `paas_file` VALUES (124, 0, 'sponge_track', '', '/sponge_track/log.urls', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：user_key,tag_id?,date?\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\nif(!_api.checkParamsIsOk(ctx,[\"udid\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar udid = ctx.param(\"udid\");\nvar tag_id   = ctx.paramAsInt(\"tag_id\",0);\nvar date     = ctx.paramAsInt(\"date\",0);\n\n\nvar tb = water.db(\"sponge/sponge_track\").table(\"short_redirect_log_30d s\")\n   .innerJoin(\"short_url u\")\n   .innerJoin(\"track_tag t\")\n   .on(\"s.url_id = u.url_id\")\n   .and(\"s.tag_id = t.tag_id\")\n   .where(\"s.v5 = ?\",udid);\n\nif(tag_id>0){\n  tb.and(\"s.tag_id = ?\", tag_id);\n}\n\nif (date > 0) {\n  tb.and(\"s.log_date = ?\",date);\n}\n\nvar list = tb.orderBy(\"s.log_fulltime desc\")\n              .limit(50)\n              .select(\"u.url_name,u.url_partner_key,t.tag_name,s.url_id,s.tag_id,s.log_date,DATE_FORMAT(s.log_fulltime,\'%Y-%m-%d %H:%i:%s\') log_fulltime\")\n              .getMapList();\n\n\nlet code = 1;\nlet msg = \'success\';\n\nreturn {code:code, msg:msg, data:list};\n', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:49:21', '2020-09-18 15:45:32', NULL);
INSERT INTO `paas_file` VALUES (125, 0, 'sponge_track', '', '/sponge_track/pui.tag.urls', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,date\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\",\"date\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar tag_id = ctx.paramAsInt(\"tag_id\");\nvar date   = ctx.paramAsInt(\"date\");\n\nvar list = water.db(\'sponge/sponge_track\')\n                .exe(`SELECT s.url_id,s.url_name,s.url_partner_key,t.pv,t.uv,t.ip FROM stat_date_hour_pv_uv_ip t INNER JOIN short_url s ON t.url_id = s.url_id WHERE t.tag_id = ${tag_id} AND t.log_date = ${date} AND t.log_hour = -1`);\n\n\nreturn {code:1,msg:\'success\',data:list};', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:49:57', '2021-10-09 14:43:08', NULL);
INSERT INTO `paas_file` VALUES (126, 0, 'sponge_track', '', '/sponge_track/pui.track.get', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：vi,vd,date_start,date_end,url_id,log_hour\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\n\nif(!_api.checkParamsIsNot0(ctx,[\"date_start\",\"date_end\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\n\nvar vi          = ctx.paramAsInt(\"vi\");\nvar vd          = ctx.param(\"vd\");\nvar notvd       = ctx.param(\"notvd\");\nvar date_start  = ctx.paramAsInt(\"date_start\");\nvar date_end    = ctx.paramAsInt(\"date_end\");\nvar url_id      = ctx.paramAsInt(\"url_id\");\nvar log_hour    = ctx.paramAsInt(\"log_hour\");\nvar inlist      = ctx.param(\"inlist\");\n\nvar selectDb = water.db(\"sponge/sponge_track\")\n                .table(\"stat_track_date_hour_pv_uv_ip\")\n         .where(\"1 = 1\")\n                .and(\"log_date >= ?\", date_start)\n                .and(\"log_date <= ?\", date_end);\nif(vd){\n   selectDb.and(\"vd = ?\", vd);\n}\nif(vi>0){\n   selectDb.and(\"vi = ?\", vi);\n}\nif(url_id==-1||url_id>0){\n   selectDb.and(\"url_id = ?\", url_id);\n}\nif (log_hour<25) {\n   selectDb.and(\"log_hour = ?\", log_hour);\n}\nif(notvd){\n   var arr = notvd.split(\',\');\n   for(var i in arr){\n     selectDb.and(\"vd != ?\", arr[i]);\n   }\n}\nif(inlist){\n   var sql = \'(\';\n   var array = inlist.split(\',\');\n   for(i in array){\n     sql = sql + \' vd = \'+array[i]+\' or\';\n   }\n   sql = sql.substring(0,sql.length-2) +\')\';\n   selectDb.and(sql);\n}\n\nvar list = selectDb.orderBy(\"log_date asc\")\n          .select(\"*\")\n                .getMapList();\n                \n\nif (list && list.length > 0) {\n  return {code:1,msg:\'success\',list:list};\n} else {\n  return {code:2,msg:\'没有数据\',list:[]};\n}', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:50:09', '2021-10-09 14:43:20', NULL);
INSERT INTO `paas_file` VALUES (127, 0, 'sponge_track', '', '/sponge_track/pui.url', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：url_partner_key,tag_id,date?\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\nif(!_api.checkParamsIsOk(ctx,[\"url_partner_key\",\"tag_id\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar upkey  = ctx.param(\"url_partner_key\");\nvar tag_id = ctx.paramAsInt(\"tag_id\");\nvar date   = ctx.paramAsInt(\"date\");\n\n\nvar db_cfg = \"sponge/sponge_track\";\n\nvar surl = water.db(db_cfg).exe(`map::SELECT * FROM short_url WHERE tag_id = ${tag_id} AND url_partner_key = \'${upkey}\'`);\n\n\nvar tb = water.db(db_cfg).table(\'stat_date_hour_pv_uv_ip\')\n                   .where(\'url_id=? AND tag_id=? AND log_hour=-1\',surl.url_id,tag_id);\n\nif(date>0){ \n    tb.and(\'log_date=?\',date);\n}\n\nvar list = tb.select(\'url_id,pv,uv,ip,log_date\').getMapList();\n\nlist.forEach(m=>{\n  m.url_name = surl.url_name;\n  m.url_partner_key = surl.url_partner_key;\n});\n\nreturn {code:1,msg:\'success\',data:list};', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:50:25', '2021-10-09 14:43:29', NULL);
INSERT INTO `paas_file` VALUES (128, 0, 'sponge_track', '', '/sponge_track/short.url.partner.key.get', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\n\nif(!_api.checkParamsIsOk(ctx,[\"url_key\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar url_key = ctx.param(\"url_key\");\n\n\nvar surl = water.db(\"sponge/sponge_track\").table(\"short_url\")\n                .where(\"url_key = ?\", url_key)\n                .select(\"url_partner_key\")\n                .getValue();\n\nreturn {code:1, msg:\'success\', data:surl};', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:50:36', '2020-06-15 14:07:07', NULL);
INSERT INTO `paas_file` VALUES (129, 0, 'sponge_track', '', '/sponge_track/url.add', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：url_partner_key,url_name,tag_id,tag_access_key\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\nlet _tool = requireX(\'/sponge_track/_tool.lib\');\n\n\nif (!_api.checkParamsIsOk(ctx,[\"url_partner_key\",\"url_name\",\"tag_id\",\"tag_access_key\"])) {\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.ip_isWhitelist(ctx)){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar tag_access_key  = ctx.param(\"tag_access_key\");\nvar tag_id          = ctx.paramAsInt(\"tag_id\",0);\nvar build_link      = ctx.param(\"build_link\");\n\nif (build_link) {\n  if (_tool.checkBuildLink(build_link) === false) {\n    return {code:6,msg:\"构建链接格式不正确\"};\n  }\n}\n\nvar tag = water.db(\"sponge/sponge_track\").table(\"track_tag\")\n      .where(\"tag_id = ?\",tag_id).and(\"tag_access_key = ?\",tag_access_key)\n      .select(\"*\")\n      .getMap();\n\n\nif (tag.tag_id === 0) {\n  return {code:5,msg:\"tag_access_key错误\"};\n}else {\n  var oNode = _tool.addShortUrlApi(ctx); \n\n  if (oNode.url) {\n    return {code:1,msg:\"success\",data:oNode};\n  }else{\n    return {code:4,msg:\"源网址已是短地址格式\"};\n  }\n}\n\n', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:50:47', '2020-09-09 18:35:38', NULL);
INSERT INTO `paas_file` VALUES (130, 0, 'sponge_track', '', '/sponge_track/url.get.by.tag.pk', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,url_partner_key\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\nif (!_api.checkParamsIsOk(ctx,[\"url_partner_key\",\"tag_id\"])) {\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar tag_id          = ctx.paramAsInt(\"tag_id\");\nvar url_partner_key = ctx.paramAsInt(\"url_partner_key\");\n\nvar obj = water.db(\"sponge/sponge_track\").table(\"short_url\")\n            .where(\"tag_id = ?\",tag_id).and(\"url_partner_key = ?\",url_partner_key)\n            .select(\"*\")\n            .getMap();\n\nreturn {code:1,msg:\"success\",data:obj};', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:50:58', '2020-06-15 14:27:27', NULL);
INSERT INTO `paas_file` VALUES (131, 0, 'sponge_track', '', '/sponge_track/url.pui.get', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,url_ids,date\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\",\"date_start\",\"date_end\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nlet tag_id      = ctx.paramAsInt(\"tag_id\");\nlet date_start  = ctx.param(\"date_start\");\nlet date_end    = ctx.param(\"date_end\");\nlet url_ids     = ctx.param(\"url_ids\",\"\");\n\nlet selectDb = water.db(\"sponge/sponge_track\")\n                .table(\"stat_date_hour_pv_uv_ip\")\n          .where(\"log_hour = -1\")\n                .and(\"tag_id = ?\", tag_id)\n                .and(\"log_date >= ?\", date_start)\n                .and(\"log_date <= ?\", date_end);\n\nif(url_ids){\n   let sql = \'(\';\n   let array = url_ids.split(\',\');\n   \n   for(let i in array){\n     sql = sql + \' url_id = \'+array[i]+\' or\';\n   }\n   \n   sql = sql.substring(0,sql.length-2) +\')\';\n   selectDb.and(sql);\n}\n\nlet list = selectDb.select(\"*\")\n                .getMapList();              \n                \nif (list && list.length > 0) {\n  return {code:1,msg:\'success\',list:list};\n} else {\n  return {code:2,msg:\'没有数据\',list:[]};\n}', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:51:09', '2021-10-09 14:48:32', NULL);
INSERT INTO `paas_file` VALUES (132, 0, 'sponge_track', '', '/sponge_track/url.update.by.tag.pk', 0, 0, 1, 1, 0, NULL, 'javascript', 'application/json', '//\n// 参数：tag_id,url_partner_key,url_val\n//\n\nlet _api = requireX(\'/_funs/_api.clz\');\n\n\nif (!_api.checkParamsIsOk(ctx,[\"url_partner_key\",\"url_val\",\"tag_id\"])) {\n return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nif(!_api.checkParamsIsNot0(ctx,[\"tag_id\"])){\n  return {code:ctx.attrMap().code, msg:ctx.attrMap().msg};\n}\n\nvar tag_id          = ctx.param(\"tag_id\");\nvar url_partner_key = ctx.paramAsInt(\"url_partner_key\");\nvar url_val         = ctx.param(\"url_val\");\n\nvar url_val_md5 = XUtil.md5(url_val);\n\nwater.db(\"sponge/sponge_track\").table(\"short_url\")\n            .where(\"tag_id = ?\",tag_id)\n            .and(\"url_partner_key = ?\",url_partner_key)\n     .set(\"url_val_md5\", url_val_md5)\n      .set(\"url_val\", url_val)\n            .set(\"update_time\", \"$NOW()\")\n            .update();\n\nreturn {code:1,msg:\"success\"};', 'ok', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 13:51:19', '2021-10-09 14:48:41', NULL);
INSERT INTO `paas_file` VALUES (133, 0, 'sponge_track', '', '/sponge_track/_tool.lib', 0, 0, 1, 1, 0, NULL, 'javascript', 'code/internal', 'this.addShortUrlApi = function(ctx){\n    var prefix = water.cfg(\'sponge/track_uri\').value;\n    \n    var tag_id          = ctx.paramAsInt(\"tag_id\",0);\n    var tag_access_key  = ctx.param(\"tag_access_key\");\n    var url_partner_key = ctx.param(\"url_partner_key\");\n    var url_name        = ctx.param(\"url_name\");\n    var url_val         = ctx.param(\"url_val\");\n    var track_params    = ctx.param(\"track_params\");\n    var trans_params    = ctx.param(\"trans_params\");\n    var user_field      = ctx.param(\"user_field\");\n    var build_link      = ctx.param(\"build_link\");\n    var note            = ctx.param(\"note\");\n    \n    if (url_val && url_val.indexOf(prefix) >= 0) { \n      return {};\n    } \n    \n    var dbx = water.db(\"sponge/sponge_track\");\n    \n    \n    var shortUrl = dbx.table(\"short_url\")\n                .where(\"tag_id = ?\",tag_id).and(\"url_partner_key = ?\",url_partner_key)\n                .select(\"*\")\n                .getMap();\n    \n    var url_id = shortUrl.url_id;\n    var trackParamsNum = 0;\n    var tag = dbx.table(\"track_tag\")\n          .where(\"tag_id = ?\",tag_id)\n         .select(\"*\")\n          .getMap();\n    \n    var tag_host = tag.tag_host;\n    if(tag_host){\n       if(tag_host.substr(-1)!=\'/\'){\n       tag_host = tag_host + \'/\';\n       }\n       prefix = tag_host;\n    }\n    var dbQueryUpdate = dbx.table(\"short_url\")\n          .set(\"tag_id\", tag_id)\n          .set(\"url_partner_key\", url_partner_key)\n          .set(\"url_name\",url_name)\n                .set(\"update_time\", \"$NOW()\");\n\n    if(track_params) {\n        trackParamsNum = track_params.split(\',\').length;\n        dbQueryUpdate.set(\"track_params\", track_params)\n          .set(\"track_params_num\", trackParamsNum);\n    } else{\n      track_params = tag.t_track_params;\n\n       if(track_params){\n        trackParamsNum = track_params.split(\',\').length;\n       }\n    }\n    \n    if(!trans_params) { \n        trans_params = tag.t_trans_params;\n    } else {\n        dbQueryUpdate.set(\"trans_params\", trans_params);\n    }\n    \n    if(!user_field) { \n        user_field = tag.t_user_field;\n    } else {\n       dbQueryUpdate.set(\"user_field\",user_field);\n    }\n    \n    if(!build_link) { \n        build_link = tag.t_build_link;\n    } else {\n        dbQueryUpdate.set(\"build_link\",build_link);\n    }\n    \n    var url_md5 = \'\';\n    if(url_val) { \n        url_md5 = XUtil.md5(url_val);\n        dbQueryUpdate.set(\"url_val\", url_val)\n                     .set(\"url_val_md5\", url_md5);\n    }\n    XUtil.log(track_params+\"a1,\"+trackParamsNum+\"a2,\"+trans_params+\"a3\");\n    var dbQuery = dbx.table(\"short_url\")\n          .set(\"tag_id\", tag_id)\n          .set(\"url_partner_key\", url_partner_key)\n          .set(\"url_name\",url_name)\n         .set(\"url_val_md5\", url_md5)\n          .set(\"url_val\", url_val)\n          .set(\"track_params\", track_params)\n          .set(\"track_params_num\", trackParamsNum)\n          .set(\"trans_params\", trans_params)\n          .set(\"user_field\",user_field)\n         .set(\"build_link\",build_link)\n                .set(\"update_time\", \"$NOW()\");\n    \n    if(!url_id){ \n       /*公共函数调用  tag名_函数名*/\n       url_id = this.getUrlID();\n       var url_key = this.getCodeByID(url_id);\n       \n      dbQuery.set(\"url_id\", url_id)\n           .set(\"create_fulltime\", \"$NOW()\")\n           .set(\"url_key\", url_key)\n             .set(\"note\",note)\n            .insert();\n      \n      dbx.table(\"short_url_ex_stat\")\n          .set(\"url_id\", url_id)\n          .set(\"tag_id\", tag_id)\n          .insert();\n          \n       return {\'url\':prefix+url_key,\'url_id\':url_id};\n    }else{\n     dbQueryUpdate.where(\"url_id = ?\", url_id).update();\n     return {\'url\':prefix+shortUrl.url_key,\'url_id\':url_id};\n    }\n}\n\n\nthis.checkBuildLink = function(build_link){\n    if(!build_link || build_link.indexOf(\'::\')<0){\n      return false;\n    }\n    \n    return true;\n}\n\nthis.getCodeByID = function(id){\n    var key = 999999999;\n    id = id + key;\n    id = id - (key / 100);\n    id = id + (key / 10000);\n    id = id - (key / 1000000);\n    id = parseInt(id + 2);\n    \n    return id.toString(36);\n}\n\nthis.getShortUrlByPartnerKey = function(url_partner_key,tag_id){\n    return water.db(\'sponge/sponge_track\')\n                .exe(`map::SELECT * FROM short_url WHERE url_partner_key=${url_partner_key} AND tag_id=${tag_id}`);\n}\n\nthis.getTag = function(tag_id){\n    return water.db(\'sponge/sponge_track\')\n                .exe(`map::SELECT * FROM track_tag WHERE tag_id=${tag_id}`);\n}\n\nthis.getUrlID = function(){\n    return rock.client.newID(\'SPONGE_ID\',\'url_id\',60 * 60 * 24 * 365) + 1000000;\n}\n\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-06-15 14:09:17', '2021-04-05 16:13:01', NULL);
INSERT INTO `paas_file` VALUES (164, 1, 'sponge_track', '', '/sponge_track/del_stat_360d', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\nvar date = Datetime.Now().addDay(-360).getDate();  //SELECT DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -360 DAY),\'%Y%m%d\')\nvar date2 = Datetime.Now().addDay(-90).getDate(); //SELECT DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -90 DAY),\'%Y%m%d\')\n\ndb2.exe(`DELETE FROM stat_city_date_pv_uv_ip WHERE log_date <?`, date);\ndb2.exe(`DELETE FROM stat_ua_client_date_pv_uv_ip WHERE log_date <?`, date);\ndb2.exe(`DELETE FROM stat_ua_platform_date_pv_uv_ip WHERE log_date <?`, date);\n\n\ndb2.exe(`DELETE FROM stat_date_hour_pv_uv_ip WHERE log_date <?  AND log_hour>=0`, date2);\n\ndb2.exe(`DELETE FROM stat_track_date_hour_pv_uv_ip WHERE log_date <?`, date2);\n\nreturn \"OK\";\n', '删除超360天的统计数据', 8, 1592064000000, 1617601110000, 107, 0, '1d', 0, 268, '2020-06-15 11:39:28', '2020-08-19 11:54:31', NULL);
