INSERT INTO `paas_file` VALUES (172, 1, 'sponge_track', '', '/sponge_track/stat_city', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\nlet date1  = new Datetime().getDate();\n\n\ndb2.exe(`UPDATE short_redirect_log_30d l, code_ip u\nSET l.log_city_code = (u.city_code/10000)*10000\nWHERE l.log_city_code=0 AND l.log_ip_id = u.ip_id AND u.city_code>0`);\n\n\ndb2.exe(`DELETE FROM stat_city_date_pv_uv_ip WHERE log_date = ?`, date1);\n\n\ndb2.exe(`INSERT INTO stat_city_date_pv_uv_ip(url_id,tag_id,province_code,log_date,pv,uv,ip)\nSELECT -1,tag_id,log_city_code,log_date,COUNT(*) pv,COUNT(DISTINCT user_key) uv,COUNT(DISTINCT log_ip_id) ip\nFROM short_redirect_log_30d \nWHERE log_date = ?\nGROUP BY tag_id,log_city_code`, date1);\n\n\ndb2.exe(`INSERT INTO stat_city_date_pv_uv_ip(url_id,tag_id,province_code,log_date,pv,uv,ip)\nSELECT url_id,tag_id,log_city_code,log_date,COUNT(*) pv,COUNT(DISTINCT user_key) uv,COUNT(DISTINCT log_ip_id) ip\nFROM short_redirect_log_30d \nWHERE log_date = ?\nGROUP BY url_id,log_city_code`, date1);\n\n\n/* update uv2 */\ndb2.exe(`UPDATE stat_city_date_pv_uv_ip t,(\n   SELECT tag_id,province_code,log_date,SUM(uv) uv2 \n   FROM stat_city_date_pv_uv_ip \n   WHERE log_date= ? AND url_id<>-1\n    GROUP BY tag_id,province_code,log_date\n) t2\nSET t.uv2 = t2.uv2\nWHERE t.tag_id = t2.tag_id AND t.province_code = t2.province_code AND t.log_date = t2.log_date AND t.url_id=-1`, date1);\n\nreturn \'OK\';\n', '统计城市数据', 8, 1592196189000, 1617601990000, 5907, 0, '5m', 0, 66714, '2020-06-15 12:10:43', '2020-08-19 11:58:51', NULL);
INSERT INTO `paas_file` VALUES (173, 1, 'sponge_track', '', '/sponge_track/stat_ua_client', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\n\nvar date1  = new Datetime().getDate();\n\n\ndb2.exe(`DELETE FROM stat_ua_client_date_pv_uv_ip WHERE log_date = ?`, date1);\n\n\ndb2.exe(`INSERT INTO stat_ua_client_date_pv_uv_ip(url_id,tag_id,ua_client,log_date,pv,uv,ip)\nSELECT -1,s.tag_id,u.client,log_date,COUNT(*) pv,COUNT(DISTINCT s.user_key) uv,COUNT(DISTINCT s.log_ip_id) ip\nFROM short_redirect_log_30d s INNER JOIN code_ua u ON s.log_ua_id=u.ua_id\nWHERE s.log_date = ?\nGROUP BY s.tag_id,u.client`, date1);\n\n\ndb2.exe(`INSERT INTO stat_ua_client_date_pv_uv_ip(url_id,tag_id,ua_client,log_date,pv,uv,ip)\nSELECT s.url_id,s.tag_id,u.client,log_date,COUNT(*) pv,COUNT(DISTINCT s.user_key) uv,COUNT(DISTINCT s.log_ip_id) ip\nFROM short_redirect_log_30d s INNER JOIN code_ua u ON s.log_ua_id=u.ua_id\nWHERE s.log_date = ?\nGROUP BY s.url_id,u.client`, date1);\n\n/* update uv2 */\ndb2.exe(`UPDATE stat_ua_client_date_pv_uv_ip t,(\n    SELECT tag_id,ua_client,log_date,SUM(uv) uv2 \n   FROM stat_ua_client_date_pv_uv_ip \n    WHERE log_date= ? AND url_id<>-1\n    GROUP BY tag_id,ua_client,log_date\n) t2\nSET t.uv2 = t2.uv2\nWHERE t.tag_id = t2.tag_id AND t.ua_client = t2.ua_client AND t.log_date = t2.log_date AND t.url_id=-1`, date1);\n\nreturn \'OK\';\n', '统计客户端UA数据', 8, 1592196194000, 1617601993000, 8925, 0, '5m', 0, 66706, '2020-06-15 12:10:57', '2020-08-19 11:59:56', NULL);
INSERT INTO `paas_file` VALUES (174, 1, 'sponge_track', '', '/sponge_track/stat_ua_platform', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\nvar date1  = new Datetime().getDate();\n\n\ndb2.exe(`DELETE FROM stat_ua_platform_date_pv_uv_ip WHERE log_date = ?`, date1);\n\n\ndb2.exe(`INSERT INTO stat_ua_platform_date_pv_uv_ip(url_id,tag_id,ua_platform,log_date,pv,uv,ip)\nSELECT -1,s.tag_id,u.platform,log_date,COUNT(*) pv,COUNT(DISTINCT s.user_key) uv,COUNT(DISTINCT s.log_ip_id) ip\nFROM short_redirect_log_30d s INNER JOIN code_ua u ON s.log_ua_id=u.ua_id\nWHERE s.log_date = ?\nGROUP BY s.tag_id,u.platform`, date1);\n\n\ndb2.exe(`INSERT INTO stat_ua_platform_date_pv_uv_ip(url_id,tag_id,ua_platform,log_date,pv,uv,ip)\nSELECT s.url_id,s.tag_id,u.platform,log_date,COUNT(*) pv,COUNT(DISTINCT s.user_key) uv,COUNT(DISTINCT s.log_ip_id) ip\nFROM short_redirect_log_30d s INNER JOIN code_ua u ON s.log_ua_id=u.ua_id\nWHERE s.log_date = ?\nGROUP BY s.url_id,u.platform`, date1);\n\n\n/* update uv2 */\ndb2.exe(`UPDATE stat_ua_platform_date_pv_uv_ip t,(\n    SELECT tag_id,ua_platform,log_date,SUM(uv) uv2 \n   FROM stat_ua_platform_date_pv_uv_ip \n    WHERE log_date= ? AND url_id<>-1\n    GROUP BY tag_id,ua_platform,log_date\n) t2\nSET t.uv2 = t2.uv2\nWHERE t.tag_id = t2.tag_id AND t.ua_platform = t2.ua_platform AND t.log_date = t2.log_date AND t.url_id=-1`, date1);\n\nreturn \'OK\';\n', '统计平台UA数据', 8, 1592196199000, 1617602000000, 2822, 0, '5m', 0, 66712, '2020-06-15 12:11:08', '2020-08-19 12:09:11', NULL);
INSERT INTO `paas_file` VALUES (175, 1, 'sponge_track', '', '/sponge_track/stat_url', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\n\nlet date1  = Datetime.Now().addDay(-1).getDate(); //$<SELECT DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -1 DAY),\'%Y%m%d\')\nlet date30 = Datetime.Now().addDay(-30).getDate(); //$<SELECT DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -30 DAY),\'%Y%m%d\')\nlet date0  = Datetime.Now().getDate(); //$<SELECT DATE_FORMAT(NOW(),\'%Y%m%d\')\nlet dateX  = 0;\nlet hour0  = Datetime.Now().getHours();\n\n\nif(hour0>=4){\n  dateX = date0;\n}else{\n  dateX = date1;\n}\n\ndb2.exe(`DELETE FROM stat_date_hour_pv_uv_ip WHERE log_date>=?`, dateX);\n\nfunction stat_url() {\n\n  /*统计典线图：日数据*/\n db2.exe(`INSERT INTO stat_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,pv,uv,ip)\n    SELECT url_id,tag_id,log_date,-1, COUNT(*) pv,COUNT(DISTINCT user_key) uv,COUNT(DISTINCT log_ip_id) ip\n    FROM short_redirect_log_30d \n    WHERE log_date>=?\n   GROUP BY url_id,log_date`, dateX);\n\n    db2.exe(`INSERT INTO stat_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,pv,uv,ip)\n    SELECT -1,tag_id,log_date,-1, COUNT(*) pv,COUNT(DISTINCT user_key) uv,COUNT(DISTINCT log_ip_id) ip\n    FROM short_redirect_log_30d \n    WHERE log_date>=?\n   GROUP BY tag_id,log_date`, dateX);\n    \n  /*update date uv2*/\n db2.exe(`UPDATE stat_date_hour_pv_uv_ip t,(\n   SELECT tag_id,log_date,SUM(uv) uv2 \n   FROM stat_date_hour_pv_uv_ip \n   WHERE log_date>= ? AND log_hour=-1 AND url_id<>-1\n   GROUP BY tag_id,log_date\n        ) t2\n      SET t.uv2 = t2.uv2\n      WHERE t.tag_id = t2.tag_id AND t.log_date = t2.log_date AND t.log_hour=-1 AND t.url_id=-1`, dateX);\n   \n\n  /*统计典线图：小时数据*/\n    db2.exe(`INSERT INTO stat_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,pv,uv,ip)\n    SELECT url_id,tag_id,log_date, log_hour,COUNT(*) pv,COUNT(DISTINCT user_key) uv,COUNT(DISTINCT log_ip_id) ip\n    FROM short_redirect_log_30d \n    WHERE log_date>=?\n   GROUP BY url_id,log_date,log_hour`, dateX);\n\n    db2.exe(`INSERT INTO stat_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,pv,uv,ip)\n   SELECT -1,tag_id,log_date, log_hour,COUNT(*) pv,COUNT(DISTINCT user_key) uv,COUNT(DISTINCT log_ip_id) ip\n    FROM short_redirect_log_30d \n    WHERE log_date>=?\n   GROUP BY tag_id,log_date,log_hour`, dateX);\n   \n  /*update hour uv2*/\n db2.exe(`UPDATE stat_date_hour_pv_uv_ip t,(\n   SELECT tag_id,log_date,log_hour,SUM(uv) uv2 \n    FROM stat_date_hour_pv_uv_ip \n   WHERE log_date>= ? AND log_hour<>-1 AND url_id<>-1\n    GROUP BY tag_id,log_date,log_hour\n        ) t2\n      SET t.uv2 = t2.uv2\n      WHERE t.tag_id = t2.tag_id AND t.log_date = t2.log_date AND t.log_hour=t2.log_hour AND t.url_id=-1`, dateX);\n}\n\nfunction stat_url_sum(){\n  /*总量统计：url记录昨日数据*/\n  db2.exe(`UPDATE short_url_ex_stat SET uv_yesterday=0,pv_yesterday=0,ip_yesterday=0 WHERE 1=1`);\n \n  db2.exe(`UPDATE short_url_ex_stat se, stat_date_hour_pv_uv_ip ss\n  SET se.uv_yesterday = ss.uv, se.pv_yesterday = ss.pv, se.ip_yesterday = ss.ip\n WHERE se.url_id = ss.url_id AND ss.log_date=? AND ss.log_hour=-1 AND ss.url_id>0`, date1);\n  \n  /*总量统计：url记录今日数据*/\n  db2.exe(`UPDATE short_url_ex_stat SET uv_today=0,pv_today=0,ip_today=0 WHERE 1=1`);\n \n  db2.exe(`UPDATE short_url_ex_stat se, stat_date_hour_pv_uv_ip ss\n  SET se.uv_today = ss.uv, se.pv_today = ss.pv, se.ip_today = ss.ip\n WHERE se.url_id = ss.url_id AND ss.log_date=? AND ss.log_hour=-1 AND ss.url_id>0`, date0);\n\n  /*总量统计：url合计总数*/\n  db2.exe(`TRUNCATE TABLE _tmp_total_pv_uv_ip`);\n  \n  db2.exe(`INSERT INTO _tmp_total_pv_uv_ip(obj_id,pv,uv,ip)\n SELECT url_id,SUM(pv) pv, SUM(uv) uv,SUM(ip) ip\n FROM stat_date_hour_pv_uv_ip WHERE log_date>=? AND log_hour=-1 AND url_id>0 GROUP BY url_id`, date30);\n\n    db2.exe(`UPDATE short_url_ex_stat SET uv_total=0,pv_total=0,ip_total=0 WHERE 1=1`);\n \n  db2.exe(`UPDATE short_url_ex_stat u, _tmp_total_pv_uv_ip s\n  SET u.pv_total = s.pv, u.uv_total = s.uv, u.ip_total = s.ip\n WHERE u.url_id = s.obj_id`);\n\n\n\n  /*总量统计：tag记录昨日数据*/\n  db2.exe(`UPDATE track_tag_ex_stat se, stat_date_hour_pv_uv_ip ss\n  SET se.uv_yesterday = ss.uv, se.pv_yesterday = ss.pv, se.ip_yesterday = ss.ip\n WHERE se.tag_id = ss.tag_id AND ss.log_date=? AND ss.log_hour=-1 AND ss.url_id=-1`, date1);\n \n  /*总量统计：tag记录今日数据*/\n  db2.exe(`UPDATE track_tag_ex_stat SET uv_today=0,pv_today=0,ip_today=0 WHERE 1=1`);\n \n  db2.exe(`UPDATE track_tag_ex_stat se, stat_date_hour_pv_uv_ip ss\n  SET se.uv_today = ss.uv, se.pv_today = ss.pv, se.ip_today = ss.ip\n WHERE se.tag_id = ss.tag_id AND ss.log_date=? AND ss.log_hour=-1 AND ss.url_id=-1`, date0);\n \n  /*总量统计：tag合计总数*/\n  db2.exe(`TRUNCATE TABLE _tmp_total_pv_uv_ip`);\n  \n  db2.exe(`INSERT INTO _tmp_total_pv_uv_ip(obj_id,pv,uv,ip)\n SELECT tag_id,SUM(pv) pv, SUM(uv) uv,SUM(ip) ip\n FROM stat_date_hour_pv_uv_ip WHERE log_date>=? AND log_hour=-1 AND url_id=-1 GROUP BY tag_id`,date30);\n  \n  db2.exe(`UPDATE track_tag_ex_stat u, _tmp_total_pv_uv_ip s\n  SET u.pv_total = s.pv, u.uv_total = s.uv, u.ip_total = s.ip\n WHERE u.tag_id = s.obj_id`);\n}\n\nstat_url();\nstat_url_sum();\n\nreturn \'OK\';\n', '统计URL数据', 8, 1592196205000, 1617602184000, 191, 0, '2m', 0, 166772, '2020-06-15 12:11:24', '2020-08-19 12:03:10', NULL);
INSERT INTO `paas_file` VALUES (176, 1, 'sponge_track', '', '/sponge_track/stat_url_track', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\nlet date1  = Datetime.Now().addDay(-1).getDate(); //$<SELECT DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -1 DAY),\'%Y%m%d\')\nlet date30 = Datetime.Now().addDay(-30).getDate(); //$<SELECT DATE_FORMAT(DATE_ADD(NOW(),INTERVAL -30 DAY),\'%Y%m%d\')\nlet date0  = Datetime.Now().getDate(); //$<SELECT DATE_FORMAT(NOW(),\'%Y%m%d\')\nlet dateX  = 0;\nlet hour0  = Datetime.Now().getHours();\n\n\nif(hour0>=4){\n  dateX = date0;\n}else{\n  dateX = date1;\n}\n\n\ndb2.exe(`DELETE FROM stat_track_date_hour_pv_uv_ip WHERE log_date>=?`, dateX);\n\nfunction stat_track(i) {\n /*url*/\n let url_ids = db2.exe(`list::SELECT url_id FROM short_url WHERE track_params_num>=?`, i).stream().map(m1 => m1.url_id).toArray();\n \n  if(url_ids){\n    for(let j in url_ids){\n        db2.exe(`INSERT INTO stat_track_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,vi,vd,uv,pv,ip)\n      SELECT url_id,tag_id,log_date,-1,${i},v${i},COUNT(DISTINCT user_key) uv,COUNT(*) pv,COUNT(DISTINCT log_ip_id) ip\n      FROM short_redirect_log_30d \n      WHERE log_date>=? AND url_id =?\n     GROUP BY url_id,log_date,v${i}`, dateX, url_ids[j]);\n      \n      \n      db2.exe(`INSERT INTO stat_track_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,vi,vd,uv,pv,ip)\n      SELECT url_id,tag_id,log_date,log_hour,${i},v${i},COUNT(DISTINCT user_key) uv,COUNT(*) pv,COUNT(DISTINCT log_ip_id) ip\n      FROM short_redirect_log_30d \n      WHERE log_date>=? AND url_id =?\n     GROUP BY url_id,log_date,log_hour,v${i}`, dateX, url_ids[j]);\n   }\n }\n\n /*tag*/\n let tag_ids = db2.exe(`list::SELECT tag_id FROM track_tag WHERE t_track_params_num>=${i}`).stream().map(m1 => m1.tag_id).toArray();\n if(tag_ids){\n    for(let j in tag_ids){\n        db2.exe(`INSERT INTO stat_track_date_hour_pv_uv_ip(url_id,tag_id,log_date,log_hour,vi,vd,uv,pv,ip)\n      SELECT -1,tag_id,log_date,-1,${i},v${i},COUNT(DISTINCT user_key) uv,COUNT(*) pv,COUNT(DISTINCT log_ip_id) ip\n      FROM short_redirect_log_30d \n      WHERE log_date>=? AND tag_id = ?\n      GROUP BY tag_id,log_date,v${i}`, dateX, tag_ids[j]);\n    }\n }\n}\n\nfunction stat_track_sum(){\n\n  /*url total*/\n db2.exe(`TRUNCATE short_url_ex_track_stat`);\n  \n  db2.exe(`INSERT INTO short_url_ex_track_stat(url_id,tag_id,vi,vd,uv_total,pv_total,ip_total)\n  SELECT url_id,tag_id,vi,vd,SUM(uv) uv,SUM(pv) pv,SUM(ip) ip \n  FROM stat_track_date_hour_pv_uv_ip \n WHERE log_date>=? AND log_hour=-1 AND url_id>0\n  GROUP BY url_id,vi,vd`, date30);\n  \n  \n  /*url yesday*/\n  db2.exe(`TRUNCATE _tmp_track_total_pv_uv_ip`);\n  \n  db2.exe(`INSERT INTO _tmp_track_total_pv_uv_ip(obj_id,vi,vd,uv,pv,ip)\n SELECT url_id,vi,vd,uv,pv,ip FROM stat_track_date_hour_pv_uv_ip \n  WHERE log_date=? AND log_hour=-1 AND url_id>0`, date1);\n \n  db2.exe(`UPDATE short_url_ex_track_stat s, _tmp_track_total_pv_uv_ip t\n  SET s.uv_yesterday = t.uv, s.pv_yesterday = t.pv, s.ip_yesterday = t.ip\n WHERE s.url_id = t.obj_id AND s.vi = t.vi AND s.vd = t.vd`);\n  \n  /*url today*/\n db2.exe(`TRUNCATE _tmp_track_total_pv_uv_ip`);\n  \n  db2.exe(`INSERT INTO _tmp_track_total_pv_uv_ip(obj_id,vi,vd,uv,pv,ip)\n SELECT url_id,vi,vd,uv,pv,ip FROM stat_track_date_hour_pv_uv_ip \n  WHERE log_date=? AND log_hour=-1 AND url_id>0`, date0);\n \n  db2.exe(`UPDATE short_url_ex_track_stat s, _tmp_track_total_pv_uv_ip t\n  SET s.uv_today = t.uv, s.pv_today = t.pv, s.ip_today = t.ip\n WHERE s.url_id = t.obj_id AND s.vi = t.vi AND s.vd = t.vd`);\n  \n  \n  /*tag total*/\n db2.exe(`TRUNCATE track_tag_ex_track_stat`);\n  \n  db2.exe(`INSERT INTO track_tag_ex_track_stat(tag_id,vi,vd,uv_total,pv_total,ip_total)\n SELECT tag_id,vi,vd,SUM(uv) uv,SUM(pv) pv,SUM(ip) ip \n FROM stat_track_date_hour_pv_uv_ip \n WHERE log_date>=? AND log_hour=-1 AND url_id=-1\n GROUP BY tag_id,vi,vd`, date30);\n  \n  \n  /*tag yesday*/\n  db2.exe(`TRUNCATE _tmp_track_total_pv_uv_ip`);\n  \n  db2.exe(`INSERT INTO _tmp_track_total_pv_uv_ip(obj_id,vi,vd,uv,pv,ip)\n SELECT tag_id,vi,vd,uv,pv,ip FROM stat_track_date_hour_pv_uv_ip \n  WHERE log_date=? AND log_hour=-1 AND url_id=-1`, date1);\n  \n  db2.exe(`UPDATE track_tag_ex_track_stat s, _tmp_track_total_pv_uv_ip t\n  SET s.uv_yesterday = t.uv, s.pv_yesterday = t.pv, s.ip_yesterday = t.ip\n WHERE s.tag_id = t.obj_id AND s.vi = t.vi AND s.vd = t.vd`);\n  \n  /*tag today*/\n db2.exe(`TRUNCATE _tmp_track_total_pv_uv_ip`);\n  \n  db2.exe(`INSERT INTO _tmp_track_total_pv_uv_ip(obj_id,vi,vd,uv,pv,ip)\n SELECT tag_id,vi,vd,uv,pv,ip FROM stat_track_date_hour_pv_uv_ip \n  WHERE log_date=? AND log_hour=-1 AND url_id=-1`, date0);\n  \n  db2.exe(`UPDATE track_tag_ex_track_stat s, _tmp_track_total_pv_uv_ip t\n  SET s.uv_today = t.uv, s.pv_today = t.pv, s.ip_today = t.ip\n WHERE s.tag_id = t.obj_id AND s.vi = t.vi AND s.vd = t.vd`);\n}\n\nstat_track(1);\nstat_track(2);\nstat_track(3);\nstat_track(4);\nstat_track(5);\n\nstat_track_sum();\n\nreturn \'OK\';\n\n', '统计URL跟踪数据', 8, 1592196211000, 1617601110000, 529, 0, '30m', 0, 11126, '2020-06-15 12:11:41', '2020-08-19 12:13:02', NULL);
INSERT INTO `paas_file` VALUES (177, 1, 'sponge_track', '', '/sponge_track/udp_user_agen_p_c', 0, 0, 1, 1, 0, NULL, 'javascript', '', 'let db2 = water.db(\'sponge/sponge_track\');\n\nvar begin_id = 0;\n\nvar list1 = db2.exe(`list::SELECT * FROM code_enum WHERE type=1 AND keyword<>\'\' ORDER BY row_id ASC`);\nvar list2 = db2.exe(`list::SELECT * FROM code_enum WHERE type=2 AND keyword<>\'\' ORDER BY row_id ASC`);\n\n/*生成平台码*/\nfor(var idx in list1){\n  var m = list1[idx];\n  \n}\n\nfor(var idx in list1){\n  var m = list1[idx];\n  var kl = m.keyword.split(\'#\');\n  for(var jdx in kl){\n var k2 = kl[jdx];\n db2.exe(`UPDATE code_ua SET platform=? WHERE ua_id>? AND platform=0 AND ua_val LIKE ?`, m.value, begin_id, k2);\n  }\n}\n\n\n\n/*生成客户端码（部份）*/\nfor(var idx in list2){\n  var m = list2[idx];\n  var kl = m.keyword.split(\'#\');\n  for(var jdx in kl){\n var k2 = kl[jdx];\n    db2.exe(`UPDATE code_ua SET client=? WHERE ua_id>? AND client=0 AND ua_val LIKE ?`, m.value, begin_id, k2);\n  }\n}\n\n\nreturn \'OK\';\n', '生成UDP数据', 8, 1592196217000, 1617601717000, 16, 0, '10m', 0, 33420, '2020-06-15 12:11:59', '2021-05-08 18:04:04', NULL);
