INSERT INTO `paas_file` VALUES (187, 2, 'water', '', '/water/jenkins_config', 0, 0, 1, 0, 0, NULL, 'freemarker', '', '<?xml version=\'1.0\' encoding=\'UTF-8\'?>\n<project>\n    <actions/>\n    <description></description>\n    <keepDependencies>false</keepDependencies>\n    <properties>\n        <hudson.model.ParametersDefinitionProperty>\n            <parameterDefinitions>\n                <hudson.model.StringParameterDefinition>\n                    <name>qq</name>\n                    <description>qqq</description>\n                    <defaultValue>qqq</defaultValue>\n                </hudson.model.StringParameterDefinition>\n            </parameterDefinitions>\n        </hudson.model.ParametersDefinitionProperty>\n    </properties>\n    <scm class=\"hudson.plugins.git.GitSCM\" plugin=\"git@3.3.0\">\n        <configVersion>2</configVersion>\n        <userRemoteConfigs>\n            <hudson.plugins.git.UserRemoteConfig>\n                <url>https://github.com/bingyue/easy-springmvc-maven.git</url>\n            </hudson.plugins.git.UserRemoteConfig>\n        </userRemoteConfigs>\n        <branches>\n            <hudson.plugins.git.BranchSpec>\n                <name>*/master</name>\n            </hudson.plugins.git.BranchSpec>\n        </branches>\n        <doGenerateSubmoduleConfigurations>false</doGenerateSubmoduleConfigurations>\n        <submoduleCfg class=\"list\"/>\n        <extensions/>\n    </scm>\n    <canRoam>true</canRoam>\n    <disabled>false</disabled>\n    <blockBuildWhenDownstreamBuilding>false</blockBuildWhenDownstreamBuilding>\n    <blockBuildWhenUpstreamBuilding>false</blockBuildWhenUpstreamBuilding>\n    <triggers/>\n    <concurrentBuild>false</concurrentBuild>\n    <builders>\n        <hudson.tasks.Shell>\n            <command>\n                echo &quot;hello world!&quot;;\n                M3_HOME=/Users/admin/Documents/tool/maven\n                PATH=$PATH:$M3_HOME/bin\n                JAVA_HOME=/Library/Java/JavaVirtualMachines/jdk1.8.0_25.jdk/Contents/Home\n                export M3_HOME;\n                export PATH;\n                export JAVA_HOME;\n                echo ${qq};\n                mvn clean install -U;\n            </command>\n        </hudson.tasks.Shell>\n    </builders>\n    <publishers/>\n    <buildWrappers/>\n</project>', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-07-03 14:21:11', '2020-07-03 14:25:46', NULL);
