INSERT INTO `paas_file` VALUES (202, 0, '_demo', '', '/_demo/message_sub', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let act = ctx.param(\'act\');\n\nlet service = \'\';           //服务名\nlet service_secretKey=\'\';   //服务签名密钥\n\nlet receiver_url =  water.cfg(\'water/paas_uri\').value + ctx.path(); //接收地址，根据情况调整\nlet alarm_mobile=\'\';\nlet receive_way = 0;\nlet topics = [\'\'];\n\nif(act === \'sub\'){\n    //订阅消息 //String subscriber_key, String receiver_url, String access_key, String alarm_mobile, int receive_way, boolean is_unstable, String... topics\n    WaterClient.Message.subscribeTopic(service,receiver_url,service_secretKey,alarm_mobile,false,0,topics);\n    return \'OK\';\n}\n\nif(act === \'unsub\'){\n    //取消订阅 //String subscriber_key, String... topics\n    WaterClient.Message.unSubscribeTopic(service,topics);\n    return \'OK\';\n}\n\n\n//接收并处理消息 //msg:{id,times,key,topic,message,sgin}\nlet result = WaterClient.Message.receiveMessage(k => ctx.param(k), service_secretKey, msg => {\n    //msg.message;\n    return false; \n});\n\n\nreturn result;\n\n//return receiver_url;', '示例', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-08-04 14:53:38', '2020-08-12 15:28:16', NULL);
INSERT INTO `paas_file` VALUES (267, 2, '_ops', '', '/_ops/peapi.sh', 0, 1, 1, 0, 0, NULL, 'sh', 'text/plain', '#!/usr/bin/bash\n\nname=\"pepperapi\"\ndpdir=/data/sss/pepper.zmapi.cn\n\nbackup(){\nbackuptime=$(date \'+%Y%m%d_%H:%M:%S\')\nbackupdir=\"/data/backup/$name/$backuptime\"\nmkdir -p \"${backupdir}\"\nscp $host:${dpdir}/pepperapi.jar ${backupdir}\n}\n\ndeploy(){\nrsync -avz --delete pepperapi.jar ${host}:${dpdir}\nssh -t root@${host} <<EOF\nsed -i \'/127.0.0.1:8081/s/^.*$/    server 127.0.0.1:8081 down;/g\'  /data/_nginx.conf/pepper.zmapi.cn \nnginx -s reload\nsleep 7\nsystemctl restart pepperapi1\nwhile :\n    do\n        curl -m 3 -sI  \"http://127.0.0.1:8081/run/check/\" | grep 200\n        if [[ $? -eq 0 ]]\n            then\n            echo \"service1 is ok\"\n            break\n        fi\n        sleep 3\n    done\nsleep 3\nsed -i \'/127.0.0.1:8081/s/^.*$/    server 127.0.0.1:8081 weight=10;/g\'  /data/_nginx.conf/pepper.zmapi.cn\nsed -i \'/127.0.0.1:8082/s/^.*$/    server 127.0.0.1:8082 down;/g\'  /data/_nginx.conf/pepper.zmapi.cn\nnginx -s reload\nsleep 7\nsystemctl restart pepperapi2\nwhile :\n    do\n        curl -m 3 -sI  \"http://127.0.0.1:8082/run/check/\" | grep 200\n        if [[ $? -eq 0 ]]\n            then\n            echo \"service2 is ok\"\n            break\n        fi\n        sleep 3\n    done\nsleep 3\nsed -i \'/127.0.0.1:8082/s/^.*$/    server 127.0.0.1:8082 weight=10;/g\'  /data/_nginx.conf/pepper.zmapi.cn\nnginx -s reload\nEOF\n}\n\nhost=api\nbackup\n\nfor host in  {api,api2}\ndo\ndeploy\ndone\nif [[ $? -eq 0 ]]\n    then\n    curl \"http://water2/run/push/?target=@alarm&msg=pepperapi发布成功\"\n    else\n    curl \"http://water2/run/push/?target=@alarm&msg=pepperapi发布失败\"\n    break\nfi\n\nrm -rf ${name}.jar\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-09-01 15:27:15', '2020-09-01 15:29:56', NULL);
INSERT INTO `paas_file` VALUES (268, 2, '_ops', '', '/_ops/rockrpc.sh', 0, 1, 1, 0, 0, NULL, 'sh', 'text/plain', '#!/usr/bin/bash\n\nname=\"rockrpc\"\ndpdir=/data/sss/water.zmapi.cn\n\nbackup(){\nbackuptime=$(date \'+%Y%m%d_%H:%M:%S\')\nbackupdir=\"/data/backup/$name/$backuptime\"\nmkdir -p \"${backupdir}\"\nscp $host1:${dpdir}/rockrpc.jar ${backupdir}\n}\n\ndeploy(){\nrsync -avz --delete rockrpc.jar ${host}:${dpdir}\nssh -t root@${host} <<EOF\nip=$(ip addr |grep inet |grep -v inet6 |grep eth0 | awk \'{print $2}\' | awk -F \"/\" \'{print $1}\')\ncurl http://water2/sev/set/?s=rockrpc@${ip}:8085,0\nsleep 2\nsystemctl restart rockrpc1\nsleep 2\ncurl http://water2/sev/set/?s=rockrpc@${ip}:8085,1\ncurl http://water2/sev/set/?s=rockrpc@${ip}:8086,0\nsleep 2\nsystemctl restart rockrpc2\nsleep 2\ncurl http://water2/sev/set/?s=rockrpc@${ip}:8086,1\nEOF\n}\n\nhost=water\nbackup\n\nfor host in  {waterapi,waterapi2}\ndo\ndeploy\ndone\nif [[ $? -eq 0 ]]\n    then\n    curl \"http://water2/run/push/?target=@alarm&msg=rockrpc发布成功\"\n    else\n    curl \"http://water2/run/push/?target=@alarm&msg=rockrpc发布失败\"\n    break\nfi\n\nrm -rf ${name}.jar\n', '', 0, NULL, NULL, 0, 0, '', 0, 0, '2020-09-01 15:28:15', '2021-05-17 17:03:18', NULL);
