INSERT INTO `paas_file` VALUES (366, 1, 'water', '', '/water/_service_consumer_sync', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let db2 = water.db(\'water/water\');\n\ndb2.exe(`UPDATE water_reg_consumer c, water_reg_service s \n            SET c.chk_fulltime = s.check_last_time,\n                c.chk_last_state = s.check_last_state,\n                c.is_enabled = s.is_enabled\n          WHERE c.consumer_address = s.address`);\n          \n          \nreturn \'OK\';', '网关-同步消费者记录', 8, 1593705600000, 1635828000956, 26, 1635827460446, '1m', 0, 5522, '2020-07-03 10:16:56', '2020-07-06 10:09:40', NULL);
INSERT INTO `paas_file` VALUES (367, 1, 'water', '', '/water/log_alarm', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let list = water.db(\"water/water\")\n                .sql(\"SELECT logger_id,tag,logger,source FROM water_cfg_logger WHERE is_alarm=1\")\n                .getMapList();\n                \n\nlet alarm_sign = XUtil.cfgGet(\'water/alarm_sign\');\n\nlet date1 = Datetime.Now().getDate();\nlet hub = Java.type(\'org.noear.water.protocol.ProtocolHub\');\n                \nfor(let m of list){\n    \n    try{\n        let key2 = \"log_num__\" + m.logger;\n        let num2_old = XUtil.cfgGet(key2);\n        \n        let num2 = hub.logQuerier.stat(m.logger,5,date1);\n        \n        if(num2){\n            if(!num2_old || (parseInt(num2_old) != num2)){\n                let msg = `预警：错误日志数量::${num2}\\\\n\\\\n[${m.logger}][${alarm_sign}]`;\n                \n                water.heihei(\'@alarm,@\'+m.tag,msg);\n                \n                XUtil.cfgSet(key2, num2+\'\');\n            }\n        }\n        \n    }catch(err){\n      XUtil.log({content:err.message});\n    }\n}\n\nreturn \'OK\';', '日志-检查错误数量并告警', 8, 1595347200000, 1635828000956, 26, 1635827460446, '1m', 0, 5536, '2020-07-23 14:12:33', '2021-10-29 14:52:05', NULL);
INSERT INTO `paas_file` VALUES (368, 1, 'water', '', '/water/_service_runtime_pull', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let db2 = water.db(\"water/water\");\n\nlet serivers = db2.table(\"water_reg_service\").whereEq(\"check_type\",0).andEq(\'is_enabled\',1).select(\"key,name,address\").getMapList();\nlet datetime = Datetime.Now();\n\n//let TrackBuffer = Java.type(\"org.noear.water.track.TrackBuffer\")\n\nfunction execDo(s1){\n   try{\n      let rst_str = XUtil.http(`http://${s1.address}/run/status/`).get();\n\n      if(rst_str && rst_str.startsWith(\"{\")){\n         let rst = JSON.parse(rst_str);\n\n         if(rst.data){\n            rst = rst.data;\n         }\n\n         if(!rst.memoryMax){\n            return;\n         }\n\n         water.logDebug(\"water_log_sev\", {tag: \"sev\", tag1:s1.address, tag2:s1.name, content:`App:: memoryMax:${rst.memoryMax} - memoryTotal:${rst.memoryTotal} - memoryUsed:${rst.memoryUsed} - threadCount:${rst.threadCount}\\\\n${rst_str}`});\n\n         //TrackBuffer.singleton().appendCount(\"_watersrt\", \"service\", `${s1.name}@${s1.address}`, rst.memoryMax, rst.memoryTotal, rst.memoryUsed, rst.threadCount);\n         \n         db2.table(\"water_reg_service_runtime\")\n            .set(\"key\",s1.key)\n            .set(\"name\",s1.name)\n            .set(\"address\",s1.address)\n            .set(\"log_date\",datetime.getDate())\n            .set(\"log_hour\",datetime.getHours())\n            .set(\"log_minute\",datetime.getMinutes())\n            .set(\"log_fulltime\",\"$NOW()\")\n            .set(\"memory_max\", rst.memoryMax)\n            .set(\"memory_total\", rst.memoryTotal)\n            .set(\"memory_used\", rst.memoryUsed)\n            .set(\"thread_peak_count\", rst.threadPeakCount)\n            .set(\"thread_count\", rst.threadCount)\n            .set(\"thread_daemon_count\", rst.threadDaemonCount)\n            .insertBy(\"key,log_date,log_hour,log_minute\");\n      }\n   }catch(err){\n      XUtil.log(err);\n   }\n}\n\nserivers.parallelStream().forEach(execDo);\n\nreturn \"OK\";', '服务-运行时信息拉取', 8, 1600048619000, 1635827999950, 191, 1635827699079, '5m', 0, 6581, '2020-09-14 09:29:30', '2021-06-08 15:08:01', NULL);
INSERT INTO `paas_file` VALUES (369, 1, 'water', '', '/water/_service_runtime_15d_del', 0, 0, 1, 0, 0, NULL, 'javascript', '', ' var db2 = water.db(\"water/water\");\n var date = Datetime.Now().addDay(-15).getDate();\n \n db2.table(\"water_reg_service_runtime\").where(\'log_date<?\',date).delete();\n\n\n return \'OK\';', '服务-运行时信息超时删除', 9, 1611072000000, 1635825679630, 55, 1635829200630, '1h', 0, 2479, '2021-01-20 14:41:39', '2021-08-18 17:26:52', NULL);
INSERT INTO `paas_file` VALUES (370, 1, 'water', '', '/water/msg_stat_sync', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let db2= water.db(\'water/water\');\n\nlet date = Datetime.Now();\n\nlet yesterday = date.addDay(-1).getDate();\n\n\nlet list1 = db2.exe(\"list::SELECT name, total_num FROM water_reg_service_speed WHERE service=? AND TAG=?\",\'_watermsg\',\'topic\');\nlet list2 = db2.exe(\"list::SELECT name, total_num FROM water_reg_service_speed_date WHERE service=? AND TAG=? AND log_date=?\",\'_watermsg\',\'topic\',yesterday);\n\nfor(let m of list1){\n    db2.table(\'water_msg_topic\')\n       .set(\'stat_msg_day_num\',m.total_num)\n       .whereEq(\'topic_name\',m.name)\n       .update();\n}\n\nfor(let m of list2){\n    db2.table(\'water_msg_topic\')\n       .set(\'stat_msg_day_num\',\'$stat_msg_day_num+\'+m.total_num)\n       .whereEq(\'topic_name\',m.name)\n       .update();\n}\n\nreturn \'OK\';\n\n', '消息统计同步', 8, 1619193600000, 1635827460818, 213, 1635827460122, '10m', 0, 4587, '2021-04-24 18:24:43', '2021-11-02 11:51:48', NULL);
INSERT INTO `paas_file` VALUES (371, 1, 'water', '', '/water/log_stat_sync', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let db2= water.db(\'water/water\');\nlet date = Datetime.Now();\n\nlet yesterday = date.addDay(-1).getDate();\nlet beforeday = date.addDay(-1).getDate();\n\nlet list1 = db2.exe(\"list::SELECT name, total_num, total_num_slow5 FROM water_reg_service_speed WHERE service=? AND TAG=?\",\'_waterlog\',\'logger\');\nlet list2 = db2.exe(\"list::SELECT name, total_num, total_num_slow5 FROM water_reg_service_speed_date WHERE service=? AND TAG=? AND log_date=?\",\'_waterlog\',\'logger\',yesterday);\nlet list3 = db2.exe(\"list::SELECT name, total_num, total_num_slow5 FROM water_reg_service_speed_date WHERE service=? AND TAG=? AND log_date=?\",\'_waterlog\',\'logger\',beforeday);\n\n\nfor(let m of list1){\n    db2.table(\'water_cfg_logger\')\n       .set(\'row_num_today\',m.total_num)\n       .set(\'row_num_today_error\',m.total_num_slow5)\n       .whereEq(\'logger\',m.name)\n       .update();\n}\n\nfor(let m of list2){\n    db2.table(\'water_cfg_logger\')\n       .set(\'row_num_yesterday\',m.total_num)\n       .set(\'row_num_yesterday_error\',m.total_num_slow5)\n       .whereEq(\'logger\',m.name)\n       .update();\n}\n\nfor(let m of list3){\n    db2.table(\'water_cfg_logger\')\n       .set(\'row_num_beforeday\',m.total_num)\n       .set(\'row_num_beforeday_error\',m.total_num_slow5)\n       .whereEq(\'logger\',m.name)\n       .update();\n}\n\nreturn \'OK\';\n\n', '日志统计同步', 9, 1619193600000, 1635827460818, 392, 1635828060818, '10m', 0, 4587, '2021-04-24 19:21:04', '2021-04-29 17:52:37', NULL);
INSERT INTO `paas_file` VALUES (372, 1, 'water', '', '/water/speed_sync', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let util = requireX(\'/water/speed_sync.fun\');\n\nlet rd = water.rd(\'water/water_redis\',5);\n\nlet rlist = rd.open1(function(ru){\n  return ru.key(\'monitor_keys\').hashGetAll();\n});\n\nvar jlist = [];\nrlist.forEach(function(k,v){\n  jlist.push(k);\n});\n\nXUtil.log(\'monitor_keys.size = \' + jlist.length);\n\nlet now = new Datetime();\n\nlet log_date = now.toString(\'yyyyMMdd\');\n\nfor(let i in jlist){\n  let ikey = jlist[i];\n  let dkey = ikey +\'$\'+ log_date;\n  \n  let dd = rd.open1((ru)=>{\n  return ru.key(dkey).hashGetAll();\n  });\n  \n  if(dd && dd.size()>0){\n    let ss = ikey.split(\'$\');\n    let mm = util.map2json(dd);\n    \n    mm.service = ss[0];\n    mm.tag = ss[1];\n    mm.name_md5 = ss[2];\n    mm.name = WaterClient.Track.getName(mm.name_md5);\n    mm.name_md5 = WaterClient.Track.getNameMd5(mm.name_md5);//兼容旧的数据\n    mm.log_fulltime = rlist.get(ikey);\n\n    if(mm.name && mm.name.length > 900){\n      mm.name = mm.name.substr(0,900);\n    }\n\n  \n    if(mm.service && mm.service != \'null\'){\n        util.speed_log(mm);\n    }\n  }\n}\n\n\nreturn \'OK\';\n\n', '接口性能-从Reids同步概况数据', 8, 1588723200000, 1635827460818, 1906, 1635827460122, '10m', 0, 4580, '2020-05-06 17:38:45', '2021-04-27 22:51:59', NULL);
INSERT INTO `paas_file` VALUES (373, 1, 'water', '', '/water/speed_sync_date', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let util = requireX(\'/water/speed_sync.fun\');\n\nlet rd = water.rd(\'water/water_redis\',5);\n\nlet rlist = rd.open1(function(ru){\n  return ru.key(\'monitor_keys\').hashGetAll();\n});\n\nvar jlist = [];\nrlist.forEach(function(k,v){\n  jlist.push(k);\n});\n\nXUtil.log(\'monitor_keys.size = \' + jlist.length);\n\nlet now = new Datetime();\n\nlet log_date = now.toString(\'yyyyMMdd\');\n\n\nfor(var i in jlist){\n  let ikey = jlist[i];\n  let dkey = ikey +\'$\'+ log_date;\n  \n  let dd = rd.open1((ru)=>{\n return ru.key(dkey).hashGetAll();\n  });\n  \n  if(dd && dd.size()>0){\n    let ss = ikey.split(\'$\');\n    let mm = util.map2json(dd);\n    \n    mm.service = ss[0];\n    mm.tag = ss[1];\n    mm.name_md5 = ss[2];\n    mm.name = WaterClient.Track.getName(mm.name_md5);\n    mm.name_md5 = WaterClient.Track.getNameMd5(mm.name_md5);//兼容旧的数据\n    mm.log_date = log_date;\n\n    if(mm.name && mm.name.length > 900){\n      mm.name = mm.name.substr(0,900);\n    }\n  \n    if(mm.service && mm.service != \'null\'){\n        util.speed_log_date(mm);\n    }\n  }\n}\n\n\n\nreturn \'OK\';\n\n', '接口性能-从Reids同步日纬度数据', 8, 1588723200000, 1635827460818, 1908, 1635827460122, '10m', 0, 4579, '2020-05-06 17:38:56', '2021-04-27 22:52:09', NULL);
INSERT INTO `paas_file` VALUES (374, 1, 'water', '', '/water/speed_sync_hour', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'let util = requireX(\'/water/speed_sync.fun\');\n\nlet rd = water.rd(\'water/water_redis\',5);\n\nlet rlist = rd.open1(function(ru){\n  return ru.key(\'monitor_keys\').hashGetAll();\n});\n\nvar jlist = [];\nrlist.forEach(function(k,v){\n  jlist.push(k);\n});\n\nXUtil.log(\'monitor_keys.size = \' + jlist.length);\n\nlet now = new Datetime();\n\nlet log_date = now.toString(\'yyyyMMdd\');\nlet log_hour = now.toString(\'HH\');\n\n\nfor(let i in jlist){\n  let ikey = jlist[i];\n  let dkey = ikey +\'$\'+ log_date + log_hour;\n  \n  let dd = rd.open1((ru)=>{\n  return ru.key(dkey).hashGetAll();\n  });\n  \n  if(dd && dd.size()>0){\n    let ss = ikey.split(\'$\');\n    let mm = util.map2json(dd);\n    \n    mm.service = ss[0];\n    mm.tag = ss[1];\n    mm.name_md5 = ss[2];\n    mm.name = WaterClient.Track.getName(mm.name_md5);\n    mm.name_md5 = WaterClient.Track.getNameMd5(mm.name_md5);//兼容旧的数据\n    mm.log_date = log_date;\n    mm.log_hour = log_hour;\n\n    if(mm.name && mm.name.length > 900){\n      mm.name = mm.name.substr(0,900);\n    }\n  \n    if(mm.service && mm.service != \'null\'){\n        util.speed_log_hour(mm);\n    }\n  }\n}\n\n\nreturn \'OK\';\n', '接口性能-从Reids同步时纬度数据', 9, 1588723200000, 1635827460818, 3892, 1635828060818, '10m', 0, 4580, '2020-05-06 17:39:05', '2021-04-27 22:53:15', NULL);
INSERT INTO `paas_file` VALUES (375, 1, 'water', '', '/water/speed_sync.fun', 0, 0, 1, 0, 0, NULL, 'javascript', '', 'this.map2json = function (map){\n  var obj = {};\n  map.forEach(function(k,v){\n    obj[k] = v;\n  });\n  \n  return obj;\n}\n\n\n\nthis.speed_log = function(m){\n    if(!m.tag || !m.name || !m.average){\n       return \'ERROR\';\n    }\n    \n    if(!m.total_num_slow1){\n      m.total_num_slow1=0;\n    }\n    \n    if(!m.total_num_slow2){\n      m.total_num_slow2=0;\n    }\n    \n    if(!m.total_num_slow5){\n      m.total_num_slow5=0;\n    }\n    \n    if(!m.slowest){\n      m.slowest=0;\n    }\n    \n    if(!m.fastest){\n      m.fastest=0;\n    }\n    \n    water.db(\'water/water\')\n      .table(\'water_reg_service_speed\')\n      .set(\'average\',m.average).set(\'fastest\',m.fastest)\n      .set(\'slowest\',m.slowest).set(\'total_num\',m.total_num)\n      .set(\'service\',m.service)\n      .set(\'tag\',m.tag)\n      .set(\'name_md5\',m.name_md5)\n      .set(\'name\',m.name)\n      .set(\'total_num_slow1\',m.total_num_slow1)\n      .set(\'total_num_slow2\',m.total_num_slow2)\n      .set(\'total_num_slow5\',m.total_num_slow5)\n      .build(function(tb){\n        if(m.log_fulltime && m.log_fulltime.indexOf(\'-\')>0){\n        tb.set(\'last_updatetime\',m.log_fulltime);\n      }\n      })\n      .upsert(\'service,tag,name_md5\');\n    \n    return \'OK\';\n}\n\nthis.speed_log_date = function(m){\n    if(!m.tag || !m.name || !m.average || !m.log_date){\n       return \'ERROR\';\n    }\n    \n    if(!m.slowest){\n      m.slowest=0;\n    }\n    \n    if(!m.fastest){\n      m.fastest=0;\n    }\n    \n    if(m.total_time){\n       m.average = parseInt(m.total_time/m.total_num);\n    }\n    \n    water.db(\'water/water\')\n      .table(\'water_reg_service_speed_date\')\n      .set(\'average\',m.average).set(\'fastest\',m.fastest)\n      .set(\'slowest\',m.slowest).set(\'total_num\',m.total_num)\n      .set(\'service\',m.service)\n      .set(\'tag\',m.tag)\n      .set(\'name\',m.name)\n      .set(\'name_md5\',m.name_md5)\n      .set(\'log_date\',m.log_date)\n      .build(function(tb){\n        if(m.total_num_slow1){\n        tb.set(\'total_num_slow1\',m.total_num_slow1);\n      }\n      \n        if(m.total_num_slow2){\n       tb.set(\'total_num_slow2\',m.total_num_slow2);\n      }\n      \n        if(m.total_num_slow5){\n       tb.set(\'total_num_slow5\',m.total_num_slow5);\n      }\n      })\n      .upsert(\'service,tag,name_md5,log_date\');\n    \n    return \'OK\';    \n}\n\n\nthis.speed_log_hour = function(m){\n    if(!m.tag || !m.name || !m.average || !m.log_date || !m.log_hour){\n       return \'ERROR\';\n    }\n    \n    if(!m.slowest){\n      m.slowest=0;\n    }\n    \n    if(!m.fastest){\n      m.fastest=0;\n    }\n    \n    water.db(\'water/water\')\n      .table(\'water_reg_service_speed_hour\')\n      .set(\'average\',m.average).set(\'fastest\',m.fastest)\n      .set(\'slowest\',m.slowest).set(\'total_num\',m.total_num)\n      .set(\'service\',m.service)\n      .set(\'tag\',m.tag)\n      .set(\'name\',m.name)\n      .set(\'name_md5\',m.name_md5)\n      .set(\'log_date\',m.log_date)\n      .set(\'log_hour\',m.log_hour)\n      .build(function(tb){\n        if(m.total_num_slow1){\n       tb.set(\'total_num_slow1\',m.total_num_slow1);\n      }\n      \n        if(m.total_num_slow2){\n       tb.set(\'total_num_slow2\',m.total_num_slow2);\n      }\n      \n        if(m.total_num_slow5){\n       tb.set(\'total_num_slow5\',m.total_num_slow5);\n      }\n      })\n      .upsert(\'service,tag,name_md5,log_date,log_hour\');\n    \n    \n    return \'OK\';    \n}\n\n\n', '接口性能-公共函数', 1, NULL, NULL, 0, 0, '1h', 0, 0, '2020-05-06 17:40:48', '2021-04-23 23:39:47', NULL);
