__global.rock = {ver:1};

rock = __global.rock;

RockClient = Java.type('sponge.rock.RockClient');
RockUtil   = Java.type('sponge.rock.RockUtil');

RockClient.tryInit(cache);


rock.client = RockClient;
rock.util   = RockUtil;

rock.app = function(appID){
    return RockClient.getApp(appID)
};

rock.cmd = function(service,cmd,args){
    var appID  = args.app;
    var appVer = args.ver;

    var appKey = RockClient.getApp(appID).app_key;

    var p = JSON.stringify(args.data);

    var token = cmd + "#" + p + "#" + appKey;
    var k = appID + "." + appVer + "." + XUtil.sha1(token, "UTF-8");


    var form = XUtil.newMap();

    form.put("p", p);
    form.put("k", k);
    form.put("cmd", cmd);

    return WaterProxy.call(service, cmd, form); 
};

rock.api = function(service,fun,args){
    return WaterProxy.call(service, fun, args);//WaterProxy
};

rock.newID = function(group,key,times){
    return rock.client.newID(group, key, times);
};

