this.addShortUrlApi = function(ctx){
    var prefix = water.cfg('sponge/track_uri').value;

    var tag_id          = ctx.paramAsInt("tag_id",0);
    var tag_access_key  = ctx.param("tag_access_key");
    var url_partner_key = ctx.param("url_partner_key");
    var url_name        = ctx.param("url_name");
    var url_val         = ctx.param("url_val");
    var track_params    = ctx.param("track_params");
    var trans_params    = ctx.param("trans_params");
    var user_field      = ctx.param("user_field");
    var build_link      = ctx.param("build_link");
    var note            = ctx.param("note");

    if (url_val && url_val.indexOf(prefix) >= 0) { 
      return {};
    } 

    var dbx = water.db("sponge/sponge_track");


    var shortUrl = dbx.table("short_url")
                .where("tag_id = ?",tag_id).and("url_partner_key = ?",url_partner_key)
                .select("*")
                .getMap();

    var url_id = shortUrl.url_id;
    var trackParamsNum = 0;
    var tag = dbx.table("track_tag")
          .where("tag_id = ?",tag_id)
         .select("*")
          .getMap();

    var tag_host = tag.tag_host;
    if(tag_host){
       if(tag_host.substr(-1)!='/'){
       tag_host = tag_host + '/';
       }
       prefix = tag_host;
    }
    var dbQueryUpdate = dbx.table("short_url")
          .set("tag_id", tag_id)
          .set("url_partner_key", url_partner_key)
          .set("url_name",url_name)
                .set("update_time", "$NOW()");

    if(track_params) {
        trackParamsNum = track_params.split(',').length;
        dbQueryUpdate.set("track_params", track_params)
          .set("track_params_num", trackParamsNum);
    } else{
      track_params = tag.t_track_params;

       if(track_params){
        trackParamsNum = track_params.split(',').length;
       }
    }

    if(!trans_params) { 
        trans_params = tag.t_trans_params;
    } else {
        dbQueryUpdate.set("trans_params", trans_params);
    }

    if(!user_field) { 
        user_field = tag.t_user_field;
    } else {
       dbQueryUpdate.set("user_field",user_field);
    }

    if(!build_link) { 
        build_link = tag.t_build_link;
    } else {
        dbQueryUpdate.set("build_link",build_link);
    }

    var url_md5 = '';
    if(url_val) { 
        url_md5 = XUtil.md5(url_val);
        dbQueryUpdate.set("url_val", url_val)
                     .set("url_val_md5", url_md5);
    }
    XUtil.log(track_params+"a1,"+trackParamsNum+"a2,"+trans_params+"a3");
    var dbQuery = dbx.table("short_url")
          .set("tag_id", tag_id)
          .set("url_partner_key", url_partner_key)
          .set("url_name",url_name)
         .set("url_val_md5", url_md5)
          .set("url_val", url_val)
          .set("track_params", track_params)
          .set("track_params_num", trackParamsNum)
          .set("trans_params", trans_params)
          .set("user_field",user_field)
         .set("build_link",build_link)
                .set("update_time", "$NOW()");

    if(!url_id){ 
       /*公共函数调用  tag名_函数名*/
       url_id = this.getUrlID();
       var url_key = this.getCodeByID(url_id);

      dbQuery.set("url_id", url_id)
           .set("create_fulltime", "$NOW()")
           .set("url_key", url_key)
             .set("note",note)
            .insert();

      dbx.table("short_url_ex_stat")
          .set("url_id", url_id)
          .set("tag_id", tag_id)
          .insert();

       return {'url':prefix+url_key,'url_id':url_id};
    }else{
     dbQueryUpdate.where("url_id = ?", url_id).update();
     return {'url':prefix+shortUrl.url_key,'url_id':url_id};
    }
}


this.checkBuildLink = function(build_link){
    if(!build_link || build_link.indexOf('::')<0){
      return false;
    }

    return true;
}

this.getCodeByID = function(id){
    var key = 999999999;
    id = id + key;
    id = id - (key / 100);
    id = id + (key / 10000);
    id = id - (key / 1000000);
    id = parseInt(id + 2);

    return id.toString(36);
}

this.getShortUrlByPartnerKey = function(url_partner_key,tag_id){
    return water.db('sponge/sponge_track')
                .exe(`map::SELECT * FROM short_url WHERE url_partner_key=${url_partner_key} AND tag_id=${tag_id}`);
}

this.getTag = function(tag_id){
