        mm.lastupdated = mm.lastupdated;
      }else{
        mm.lastupdated = Datetime.parse(mm.lastupdated, "yyyy-MM-dd HH:mm:ss").getTicks();
      }
    }

    if(mm.name && mm.name.length > 900){
      mm.name = mm.name.substr(0,900);
